import React, { Component } from 'react';
import Pdf from "react-to-pdf";
import './styles/Facture.scss';
import {
    connect
} from 'react-redux';
import { Link } from '@material-ui/core';

class Facture extends Component {
    render() {
       const ref = React.createRef();
        return ( 
            <div className="Facture">
                <div>
                    <div style={{display:flex}}>
                        <Link to='/GReglements'>Retour</Link>
                        <Pdf targetRef = { ref } filename = "facture.pdf" > 
                            {
                                ({ toPdf }) => < button onClick = {  toPdf } > Generate Pdf </button>
                            } 
                        </Pdf>
                    </div> 
                    <div ref={ref}>
                        <div>
                            <h1> SALEM HOTEL </h1>   
                            <h2> Facture pour vous </h2> 
                            Num: {
                                this.props.match.params.numReglement
                                } <br/>
                            Montant Recu: {
                                    "1000"
                                }
                        </div>
                        <div>
                            <span style={{fontFamily: 'purisa',fontSize:20}}> merci </span> de votre visite
                        </div>
                    </div> 
                </div>
            </div>
        );
    }
}
const mapStateToProps = state => {
    return {
        reserver: state.reserver,
        chambres: state.chambres,
        concerner: state.concerner
    }
}

const mapDispatchToProps = dispatch => {
    return {

    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Facture);
